                    return mCurrentVariableState;
                }
                set
                {
                    mCurrentVariableState = value;
                    switch(mCurrentVariableState)
                    {
                        case  VariableState.Default:
                            ListBoxInstance.Height = 100f;
                            ListBoxInstance.HeightUnits = Gum.DataTypes.DimensionUnitType.Percentage;
                            ListBoxInstance.Width = 100f;
                            ListBoxInstance.WidthUnits = Gum.DataTypes.DimensionUnitType.Percentage;
